package array;

public class ArrayEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int []a= {12,3,45,'e',67};
for(int x:a) {
	System.out.println(x);
}

String []s=new String[] {"aa","df","rtr"};
for(String x:s) {
	System.out.println(x);
}

float f[]= {12.3f,54f,5f,344f,2f};
for(float z:f) {
	System.out.println(z);
}

double d[]= {45.4,34.4,64.4,34.0};
for(double x:d) {
	System.out.println(x);
}

char c[]= {'w','d','r','e'};
for(char y:c) {
	System.out.println(y);
}
	}

}
